import { useParams, Route, Link, useRouteMatch } from 'react-router-dom';
import { Fragment } from 'react';
import Comments from '../components/comments/Comments';
import HighlightedQuote from './../components/quotes/HighlightedQuote';

const DUMMY_QUOTES = [
  {
    id: 'q1',
    author: 'Peter',
    text: 'Learning is hard, not learning is harder!',
  },
  {
    id: 'q2',
    author: 'John',
    text: 'Reality leaves a lot to the imagination.',
  },
  {
    id: 'q3',
    author: 'George',
    text: 'Life goes on within you and without you.',
  },
  {
    id: 'q4',
    author: 'Augustinus von Hippo',
    text: "If the wind won't serve, take to the oars!",
  },
];

const QuotesDetail = () => {
  const match = useRouteMatch();
  const params = useParams();

  const quote = DUMMY_QUOTES.find((quote) => quote.id === params.quoteId);

  if (!quote) {
    return <p>No quote found.</p>;
  }
  return (
    <Fragment>
      <HighlightedQuote text={quote.text} author={quote.author} />
      <Route path={match.path} exact>
        <div className='centered'>
          <Link className='btn--flat' to={`${match.url}/comments`}>
            load comments
          </Link>
        </div>
      </Route>

      <Route path={`${match.path}/comments`}>
        <Comments />
      </Route>
    </Fragment>
  );
};
export default QuotesDetail;
