import { useEffect } from 'react';
import QuoteList from '../components/quotes/QuoteList';
import useHttp from './../hooks/use-http';
import { getAllQuotes } from '../lib/api';

const DUMMY_QUOTES = [
  {
    id: 'q1',
    author: 'Peter',
    text: 'Learning is hard, not learning is harder!',
  },
  {
    id: 'q2',
    author: 'John',
    text: 'Reality leaves a lot to the imagination.',
  },
  {
    id: 'q3',
    author: 'George',
    text: 'Life goes on within you and without you.',
  },
  {
    id: 'q4',
    author: 'Augustinus von Hippo',
    text: "If the wind won't serve, take to the oars!",
  },
];

const AllQuotes = () => {
  const { sndRequest, status, data: loadedQuotes, error } = useHttp(
    getAllQuotes,
    true
  );

  useEffect(() => {
    sendRequest();
  }, [sendRequest]);
  return <QuoteList quotes={DUMMY_QUOTES} />;
};
export default AllQuotes;
